import React from 'react';
import { View, Text, Button, FlatList, StyleSheet } from 'react-native';
import { calculateDistanceKm } from '../utils/distance';

const TRUCKS = [
  { id: 't1', name: 'Small Truck', baseFare: 10, perKm: 1.5 },
  { id: 't2', name: 'Medium Truck', baseFare: 20, perKm: 2.0 },
  { id: 't3', name: 'Large Truck', baseFare: 35, perKm: 3.0 }
];

export default function SelectTruckScreen({ route, navigation }) {
  const { origin, destination } = route.params;
  const distanceKm = calculateDistanceKm(origin.lat, origin.lng, destination.lat, destination.lng);

  const onSelect = (truck) => {
    const amount = truck.baseFare + truck.perKm * distanceKm;
    navigation.navigate('Confirmation', { truck, origin, destination, distanceKm, amount });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Select Truck</Text>
      <Text>Distance: {distanceKm.toFixed(2)} km</Text>
      <FlatList
        data={TRUCKS}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <Text style={styles.truckName}>{item.name}</Text>
            <Text>Base: ${item.baseFare}  â€¢  ${item.perKm}/km</Text>
            <Button title="Book" onPress={() => onSelect(item)} />
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 16, backgroundColor: '#fff' },
  title: { fontSize: 20, fontWeight: 'bold', marginBottom: 12 },
  item: { padding: 12, borderBottomWidth: 1, borderColor: '#eee' },
  truckName: { fontWeight: '600' }
});
